import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from 'react';
import { ScaleLoader } from 'react-spinners';
import './App.css';

function App(){

  const[prompt,setPrompt] = useState('');
  const[messages,setMessages] = useState([]);
  const[loading,setLoading] = useState(false);

  const handleSubmit = async(e) => {

    e.preventDefault();
    setLoading(true);
    const userMessage = {text:prompt, user:true};
    setMessages((prevMessages) => [...prevMessages,userMessage]);
    setPrompt('');

    try{
      const res = await axios.post('http://localhost:5000/generate',{prompt});
      const aiMessage = {text:res.data.reponse,user:false};
      setMessages((prevMessages) => [...prevMessages,aiMessage]);
    }
    catch(error){
      console.error('Error calling the server:', error);
      const errorMessage = { text: 'Error: Unable to fetch response', user: false };
      setMessages((prevMessages) => [...prevMessages, errorMessage]);
    }
    finally{
        setLoading(false);
    }
  }
  return(

    <div className='App'>

      <img className='fullscreen-image' src="./images/ae.jpg" alt=''/>
      <h2 style={{textAlign:'left'}}>Chat with AI</h2>
      <div className='chat-container'>

        {messages.map((message,index) => (
          <div key={index} className={`message ${message.user ? 'user' : 'ai'}`}
          dangerouslySetInnerHTML={{__html: message.text}}/>
        ))}
        {loading && (
          <div className="message ai">
            <ScaleLoader color={"#123abc"} loading={loading} />
          </div>
        )}
      </div>

      <form onSubmit={handleSubmit} className='input-container'>
          <input type="text" value={prompt} placeholder='Enter your message...' onChange={(e) => setPrompt(e.target.value)} required/>
      </form>
    </div>

  )
}

export default App;
