import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useRef, useState } from 'react';
import { ScaleLoader } from 'react-spinners';
import './App.css';

function App() {

  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);
  const responseBoxRef = useRef(null); // Reference to the response box

  const handleSubmit = async (e) => {
    e.preventDefault();

    setLoading(true);  // Set loading to true when request is sent

    try {
      const res = await axios.post('http://localhost:5000/generate', { prompt });
      setResponse(res.data.response);
    } catch (error) {
      console.error('Error calling the server:', error);
    } finally {
      setLoading(false);  // Set loading to false once the response is received
    }
  };

  // Function to copy the HTML content inside the response box
  const copyToClipboard = () => {
    if (responseBoxRef.current) {
      const htmlContent = responseBoxRef.current.innerHTML; // Get the inner HTML of the response box
      const tempElement = document.createElement('textarea'); // Create a temporary textarea to hold the HTML content
      tempElement.value = htmlContent;
      document.body.appendChild(tempElement);
      tempElement.select();
      document.execCommand('copy'); // Copy the HTML content to the clipboard
      document.body.removeChild(tempElement); // Remove the temporary textarea

      alert("HTML content copied to clipboard!");
    }
  };

  return (
    <div className="App">
      <img className="fullscreen-image" src="./images/ae.jpg" alt='' />
      <h2 style={{ textAlign: 'left' }}>Chat With AI</h2><br/>
      <form onSubmit={handleSubmit}>
        <input className='inputdesign'
          type="text"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Enter your prompt"
          required
        /><br/><br/>
        <button className="btn btn-primary" type="submit">Send</button>
      </form><br/>

      {loading && (
        <center>
          <ScaleLoader color={"#123abc"} loading={loading} />
        </center>
      )}

      {/* Response box with copy button */}
      {response && !loading && (
        <center>
          <div className="response-container" style={{ position: 'relative', display: 'inline-block' }}>
            <div ref={responseBoxRef} className="response-box" dangerouslySetInnerHTML={{ __html: response }} />

            {/* Copy Button positioned at the top right */}
            <button 
              className="btn btn-secondary copy-button" 
              onClick={copyToClipboard}
              style={{
                position: 'absolute',
                top: '10px',
                right: '10px',
                zIndex: '1'
              }}
            >
              Copy
            </button>
          </div>
        </center>
      )}
    </div>
  );
}

export default App;
