import axios from 'axios';
import React, { useState } from 'react';
import './App.css';

function App() {

  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post('http://localhost:5000/generate', { prompt });
      setResponse(res.data.response);
    } catch (error) {
      console.error('Error calling the server:', error);
    }
  };


  return (
    <div className="App">
      <img className="fullscreen-image" src="./images/ae.jpg" alt='' />
      <h1>Google Gemini AI Prompt</h1>
      <form onSubmit={handleSubmit}>
        <input className='inputdesign'
          type="text"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Enter your prompt"
          required
        /><br/><br/>
        <button className="btn btn-success" type="submit">Send</button>
      </form>
      {response && <div><h2>Response from AI:</h2><p>{response}</p></div>}
    </div>
  );
}

export default App;
